var ui = (function ($, document, window, undefined) {
    var self = Object.create({});

    var select = function(callback) {
        $('select').selectpicker();

        if (typeof callback === "function")
            callback();
    };

    var password = function(callback) {
        $('.password-show').on('click', function(){
            if($(this).children('div').hasClass('switch-on'))
                $('input#password').attr('type', 'text');
            else
                $('input#password').attr('type', 'password');
        });

        if (typeof callback === "function")
            callback();
    };

    var setWifiSwitch = function(strength, callback) {
        switch (strength) {
            case 1:
                setWifiNill();
                break;
            case 2:
                setWifiLow();
                break;
            case 3:
                setWifiMedium();
                break;
            case 4:
                setWifiFull();
                break;

        }

        if (typeof callback === "function")
            callback();
    };

    var setIpSwitch = function(ip, callback) {
        $('[data-id="ip-address"]').text(ip);

        if (typeof callback === "function")
            callback();
    };

    var setUserSwitch = function(count, callback) {
        $('[data-id="user-count"]').text(count);

        if (typeof callback === "function")
            callback();
    };

    var setNetworkSpeedSwitch = function(speed, callback) {
        $('[data-id="signal-strength-speed"]').text(speed + "mBit");
    }

    var setWifiNill = function(callback) {
        $('[data-id="wifi-bars"] > [data-id="low"], [data-id="wifi-bars"] > [data-id="medium"], [data-id="wifi-bars"] > [data-id="full"]').addClass('disabled');
        $('[data-id="wifi-bars"] > [data-id="nill"]').removeClass('disabled');
        $('[data-id="signal-strength-text"]').text('Zwak');

        if (typeof callback === "function")
            callback();
    }

    var setWifiLow = function(callback) {
        $('[data-id="wifi-bars"] > [data-id="medium"], [data-id="wifi-bars"] > [data-id="full"]').addClass('disabled');
        $('[data-id="wifi-bars"] > [data-id="nill"], [data-id="wifi-bars"] > [data-id="low"]').removeClass('disabled');
        $('[data-id="signal-strength-text"]').text('Laag');

        if (typeof callback === "function")
            callback();
    }

    var setWifiMedium = function(callback) {
        $('[data-id="wifi-bars"] > [data-id="full"]').addClass('disabled');
        $('[data-id="wifi-bars"] > [data-id="nill"], [data-id="wifi-bars"] > [data-id="low"], [data-id="wifi-bars"] > [data-id="medium"]').removeClass('disabled');
        $('[data-id="signal-strength-text"]').text('Matig');

        if (typeof callback === "function")
            callback();
    }

    var setWifiFull = function(callback) {
        $('[data-id="wifi-bars"] > [data-id="nill"], [data-id="wifi-bars"] > [data-id="low"], [data-id="wifi-bars"] > [data-id="medium"], [data-id="wifi-bars"] > [data-id="full"]').removeClass('disabled');
        $('[data-id="signal-strength-text"]').text('Sterk');

        if (typeof callback === "function")
            callback();
    }

    self.setWifi = function(strength, callback) {
        setWifiSwitch(strength);

        if (typeof callback === "function")
            callback();
    };

    self.setIp = function(ip, callback) {
        setIpSwitch(ip);

        if (typeof callback === "function")
            callback();
    };

    self.setUser = function(count, callback) {
        setUserSwitch(count);

        if (typeof callback === "function")
            callback();
    };

    self.setNetworkSpeed = function(speed, callback) {
        setNetworkSpeedSwitch(speed);

        if (typeof callback === "function")
            callback();
    };

    (function () {
        window.onload = function () {
            select();
            password();
            self.setWifi(2);
            self.setIp('127.0.0.1');
            self.setUser(6);
            self.setNetworkSpeed(150);
        };
    }());

    return self;
})(jQuery, document, window);